# Stores visible over MP:
var looping = 1;
var str5 = "";
var split1 = nil;
var split2 = nil;
var split3 = nil;
var pylon = nil;
var type = nil;
var count = nil;
var set = nil;

var loop = func {
  #print("loop");
  str5 = self.getNode("sim/multiplay/generic/string[5]");
  if (str5 != nil and str5.getValue() != nil and size(str5.getValue()) > 10) {#print("pass1 "~str5.getValue());
    split1 = split("++", str5.getValue());
    if (size(split1) > 1) {#print("pass2");
      split2 = split("--", split1[0]);
      split3 = split("--", split1[1]);
      if (split2 != nil and size(split2)> 1 and split3 != nil and size(split3)> 1) {#print("pass3");
        pylon = num(split2[0]);
        type = split2[1];
        count = num(split3[0]);
        set = split3[1];
        self.getNode("payload/armament/station/id-"~pylon~"-type",1).setValue(type);
        self.getNode("payload/armament/station/id-"~pylon~"-count",1).setIntValue(count);
        self.getNode("payload/armament/station/id-"~pylon~"-set",1).setValue(set);
      }
    }
  }
  if (looping) settimer(loop, 0.10);
}

loop();
