binding.script:
if (getprop("/sim/current-view/can-change-z-offset")) {
    var distance = getprop("/sim/current-view/z-offset-m");
    var multiple = getprop("/sim/current-view/z-offset-inc-step");
    var min_dist = getprop("/sim/current-view/z-offset-min-m");

    distance = math.round(std.min(-min_dist, distance + multiple) / multiple) * multiple;
    setprop("/sim/current-view/z-offset-m", distance);

    gui.popupTip(sprintf("%d meters", abs(distance)));
} else {
    view.decrease();
}

if (getprop("/sim/current-view/can-change-z-offset")) {
    var distance = getprop("/sim/current-view/z-offset-m");
    var multiple = getprop("/sim/current-view/z-offset-dec-step");
    var max_dist = getprop("/sim/current-view/z-offset-max-m");

    distance = math.round(std.max(-max_dist, distance + multiple) / multiple) * multiple;
    setprop("/sim/current-view/z-offset-m", distance);

    gui.popupTip(sprintf("%d meters", abs(distance)));
} else {
    view.increase();
}

if (getprop("/sim/current-view/can-change-z-offset")) {
    var distance = getprop("/sim/current-view/z-offset-m");
    var multiple = getprop("/sim/current-view/z-offset-dec-step");
    var max_dist = getprop("/sim/current-view/z-offset-max-m");

    distance = math.round(std.max(-max_dist, distance + multiple) / multiple) * multiple;
    setprop("/sim/current-view/z-offset-m", distance);

    gui.popupTip(sprintf("%d meters", abs(distance)));
} else {
    view.increase();
}

if (getprop("/sim/current-view/can-change-z-offset")) {
    var distance = getprop("/sim/current-view/z-offset-m");
    var multiple = getprop("/sim/current-view/z-offset-inc-step");
    var min_dist = getprop("/sim/current-view/z-offset-min-m");

    distance = math.round(std.min(-min_dist, distance + multiple) / multiple) * multiple;
    setprop("/sim/current-view/z-offset-m", distance);

    gui.popupTip(sprintf("%d meters", abs(distance)));
} else {
    view.decrease();
}

if (getprop("payload/armament/msg")==1) {
    screen.log.write("CTRL-U disabled at the moment.");
} else {
    setprop("position/altitude-ft", getprop("position/altitude-ft")+1000);
    setprop("sim/startup/onground", 0);
}

if (getprop("payload/armament/msg")==1) {
    screen.log.write("Speed-up disabled at the moment.");
} else {
    controls.speedup(1);
}

if (getprop("payload/armament/msg")==1) {
    screen.log.write("Speed-down disabled at the moment.");
} else {
    controls.speedup(-1);
}

if (getprop("sim/variant-id") <= 5 and getprop("controls/armament/master-arm") == 0 and getprop("controls/armament/master-arm-cover-open") == 0) {
    setprop("controls/armament/master-arm-cover-open",1);
    screen.log.write("Master-arm cover open.", 0.5, 0.5, 1);
} else {
    setprop("controls/armament/master-arm",!getprop("controls/armament/master-arm"));
    screen.log.write("Master-arm "~(getprop("controls/armament/master-arm")==0?"OFF":"ON"), 0.5, 0.5, 1);
}
